$(function(){

	// run function on all dialog opens
	$(document).on('dialogopen', '.ui-dialog', function(event, ui) {
		fluidDialog();
	});

	// remove window resize namespace
	$(document).on('dialogclose', '.ui-dialog', function(event, ui) {
		$(window).off('resize.responsive');
	});

	var fluidDialog = function() {
		// each open dialog
		$('.ui-dialog:visible').each(function() {
			var $this = $(this);
			var $dialog = $this.find('.ui-dialog-content');
			// if fluid option == true
			if ($dialog.dialog('option', 'maxWidth') && $dialog.dialog('option', 'width')) {
				// fix maxWidth bug
				$this.css('max-width', $dialog.dialog('option', 'maxWidth'));
				//reposition dialog
				$dialog.dialog('option', 'position', $dialog.dialog('option', 'position'));
			}

			if ($dialog.dialog('option', 'fluid')) {
				// namespace window resize
				$(window).on('resize.responsive', function() {
					var wWidth = $(window).width();
					// check window width against dialog width
					if (wWidth < $dialog.dialog('option', 'maxWidth') + 50) {
						// keep dialog from filling entire screen
						$this.css('width', '90%');

					}
					
					if($dialog.dialog('option', 'height') === undefined){
						var hHeight = $(window).height();
						// check window height against dialog height
						if (hHeight < $this.height() + 50) {
							// keep dialog from filling entire screen
							$this.css('height', '90%');
							$dialog.css('max-height', $this.height() - $this.find('.ui-dialog-titlebar').height());
						} else {
							$this.css('height', 'auto');
							$dialog.css('max-height', $dialog.css('height'));
						}
					}
					
					//reposition dialog
					$dialog.dialog('option', 'position', $dialog.dialog('option', 'position'));
				});
				$(window).trigger('resize.responsive');
			}

		});
	};

	$('.lightbox-trigger').click(function() {
		if($(this).attr('data-element') !== undefined){
			$('#' + $(this).attr('data-element')).each(function(i, el) {
				var _this = $(el);
				
				var dialogWidth;
				if (_this.attr('data-width') !== undefined) {
					dialogWidth = parseInt(_this.attr('data-width'));
				} else {
					dialogWidth = 600;
				} // End else
				
				var dialogHeight;
				if (_this.attr('data-height') !== undefined) {
					dialogHeight = parseInt(_this.attr('data-height'));
				} 

				_this.dialog({
					dialogClass : 'bbva-lightbox',
					title: _this.attr('data-title'),
					autoOpen : true,
					resizable : false,
					draggable: false,
					modal : true,
					fluid : true, //new option
					width : 'auto',
					maxWidth : dialogWidth,
					height: dialogHeight,
					open : function(event, ui) {
						$(document.body).css('overflow', 'hidden');
						fluidDialog(); // needed when autoOpen is set to true in this codepen
					},
					close : function() {
						$(document.body).css('overflow', 'auto');
					}
				}); // End dialog
			}); // End each
		} else if($(this).attr('data-href') !== undefined || $(this).attr('href') !== undefined){
			var url = $(this).attr('data-href');
			if(url === undefined){
				url = $(this).attr('href');
			}
			
			$.ajax({
				url: url,
				success: function(html) {
					var _this = $(html);
					var dialogWidth;

					if (_this.attr('data-width') !== undefined) {
						dialogWidth = parseInt(_this.attr('data-width'));
					} else {
						dialogWidth = 600;
					}
					
					var dialogHeight;
					if (_this.attr('data-height') !== undefined) {
						dialogHeight = parseInt(_this.attr('data-height'));
					} 

					_this.dialog({
						dialogClass : 'bbva-lightbox',
						title: _this.attr('data-title'),
						autoOpen : true,
						resizable : false,
						draggable: false,
						modal : true,
						fluid : true, //new option
						width : 'auto',
						maxWidth : dialogWidth,
						height: dialogHeight,
						open : function(event, ui) {
							$(document.body).css('overflow', 'hidden');
							fluidDialog(); // needed when autoOpen is set to true in this codepen
							
							$(document).trigger('capgemini.ajax.load', _this);
						},
						close : function() {
							$(document.body).css('overflow', 'auto');
							_this.remove();
						}
					}); // End dialog
				}
			});
		}
		return false;
	}); // End click
	
}); // End ready
/*
 * Lightbox object based on bootstrap modal
 */
$(function(){
	$(document).on('click', '.modal-trigger', function(){
		var attrs = $(this).dataAttrs();
		var href = $(this).attr('href');
		if(href === undefined){
			href = attrs['href'];
		}
		if(href !== undefined){
			$.ajax({
				url: href, 
				success: function(data){
					// Create modal layer and append received content
					var $modal = $('<div class="modal ' + (attrs['transition'] === undefined ? '':attrs['transition']) + '" tabindex="-1" role="dialog" aria-hidden="true"></div>');
					$modal.append(data);
					
					// Append to body
					$(document.body).append($modal);
					
					if(Modernizr.touch){
						$modal.on('show.bs.modal', function() {
						    // Position modal absolute and bump it down to the scrollPosition
						    $(this)
						        .css({
						            position: 'absolute',
						            marginTop: $(window).scrollTop() + 'px',
						            bottom: 'auto'
						        });
						    // Position backdrop absolute and make it span the entire page
						    //
						    // Also dirty, but we need to tap into the backdrop after Boostrap 
						    // positions it but before transitions finish.
						    //
						    setTimeout( function() {
						        $('.modal-backdrop').css({
						            position: 'absolute', 
						            top: 0, 
						            left: 0,
						            width: '100%',
						            height: Math.max(
						                document.body.scrollHeight, document.documentElement.scrollHeight,
						                document.body.offsetHeight, document.documentElement.offsetHeight,
						                document.body.clientHeight, document.documentElement.clientHeight
						            ) + 'px'
						        });
						    }, 0);
						});
					}
					
					$modal.on('shown.bs.modal', function (event) {
						// trigger ajax load (on shown)
						$(document).trigger('capgemini.ajax.load', $modal);
						
						// Prevent focus outside modal 
						$('*').on('focus', function(event){
							if($(event.target).closest('.modal') == 0){
								event.preventDefault();
								return false;
							}
						});
					}).on('hidden.bs.modal', function (event) {
						// Remove it on close (on hidden)
						$modal.remove();
						
						// Remove focus fix handler
						$('*').off('focus');
					});
					
					// Open modal
					$modal.modal({
						backdrop: 'static'
					});
				}
			});
		}
		
		return false;
	});
});
/*
 * David Valls Roure (Capgemini - 2013)
 * Moves a layer from one place to another based on device. Usage:
 * <div class="move-to" data-device="[phone,tablet,desktop]" data-element="unique-identifier"></div>
 * <div class="move-to" data-device="[phone,tablet,desktop]" data-element="unique-identifier"></div>
 * <div class="move-to" data-device="[phone,tablet,desktop]" data-element="unique-identifier"></div>
 */

$(function(){
	var moveTo = function(){
		if(Modernizr.mq('(max-width:480px)')){
			// Phone
			$('.move-to[data-device*="phone"]').each(function(){
				var element = $(this).attr('data-element');
				if($.trim($(this).html()) == ''){
					var $element = $('.move-to[data-device*="tablet"][data-element=' + element + ']').children();
					if($element.length == 0){
						var $element = $('.move-to[data-device*="desktop"][data-element=' + element + ']').children();
					}
					$(this).append($element);
				}
			});
		} else if(Modernizr.mq('(min-width:481px) and (max-width:768px)')){
			// Tablet
			$('.move-to[data-device*="tablet"]').each(function(){
				var element = $(this).attr('data-element');
				if($.trim($(this).html()) == ''){
					var $element = $('.move-to[data-device*="phone"][data-element=' + element + ']').children();
					if($element.length == 0){
						var $element = $('.move-to[data-device*="desktop"][data-element=' + element + ']').children();
					}
					$(this).append($element);
				}
			});
		} else {
			// Desktop
			$('.move-to[data-device="desktop"]').each(function(){
				var element = $(this).attr('data-element');
				if($.trim($(this).html()) == ''){
					var $element = $('.move-to[data-device*="tablet"][data-element=' + element + ']').children();
					if($element.length == 0){
						var $element = $('.move-to[data-device*="phone"][data-element=' + element + ']').children();
					}
					$(this).append($element);
				}
			});
		}
	};
	
	moveTo();
	$(window).on('throttledresize', function(){
		moveTo();
	});
});
