/*global window, $ */
$(function () {
    'use strict';
    
    var initFileUpload = function($context){
    
	    // Change this to the location of your server-side upload handler:
	    var url = window.location.hostname === 'blueimp.github.io' ?
	                '//jquery-file-upload.appspot.com/' : 'fileupload/',
	        uploadButton = $('<button/>')
	            .addClass('btn btn-primary')
	            .prop('disabled', true)
	            .text('Processing...')
	            .on('click', function () {
	                var $this = $(this),
	                    data = $this.data();
	                $this
	                    .off('click')
	                    .text('Abortar')
	                    .on('click', function () {
	                        $this.remove();
	                        data.abort();
	                    });
	                data.submit().always(function () {
	                    $this.remove();
	                });
	            });
	    $('#fileupload').fileupload({
	        url: url,
	        dataType: 'json',
	        autoUpload: true,
	        acceptFileTypes: /(\.|\/)(gif|jpe?g|png)$/i,
	        maxFileSize: 5000000, // 5 MB
	        // Enable image resizing, except for Android and Opera,
	        // which actually support image resizing, but fail to
	        // send Blob objects via XHR requests:
	        disableImageResize: /Android(?!.*Chrome)|Opera/
	            .test(window.navigator.userAgent),
	        disableImagePreview: true,
	        previewMaxWidth: 100,
	        previewMaxHeight: 100,
	        previewCrop: true
	    }).on('fileuploadadd', function (e, data) {
	        data.context = $('<div class="file"/>').appendTo('#files');
	        $.each(data.files, function (index, file) {
	        	var fileName = file.name;
	        	if(fileName.length > 40) {
	        		console.log(fileName.length);
	        		fileName = fileName.substring(0, 40) + " ...";
	        	}
	        	
	        	var node = $('<p/>').append($('<span class="name table-button"/>').text(fileName));
	            if (!index) {
	                node
	                    .append(uploadButton.clone(true).data(data));
	            }
	            node.appendTo(data.context);
	        });
	    }).on('fileuploadprocessalways', function (e, data) {
	        var index = data.index,
	            file = data.files[index],
	            node = $(data.context.children()[index]);
	        if (file.preview) {
	            node
	                .prepend('<br>')
	                .prepend(file.preview);
	        }
	        if (file.error) {
	            node
	                .append('<br>')
	                .append($('<span class="text-danger"/>').text(file.error));
	        }
	        if (index + 1 === data.files.length) {
	            data.context.find('button')
	                .text('Subir').hide();
	                //.prop('disabled', !!data.files.error);
	        }
	    }).on('fileuploadprogressall', function (e, data) {
	        var progress = parseInt(data.loaded / data.total * 100, 10);
	        $('#progress .progress-bar').css(
	            'width',
	            progress + '%'
	        );
	    }).on('fileuploaddone', function (e, data) {
	        $.each(data.result.files, function (index, file) {
	            if (file.url) {
	                var link = $('<a>').attr('target', '_blank').prop('href', file.url);
	                $(data.context.children()[index]).find('.name').wrap(link);
	                var fileSize = Math.round(file.size / 1024);
	                $(data.context.children()[index]).append($('<span class="size"/>').text(fileSize + ' Kb'));
	                
	                var deleteButton = $('<a title="Anular" class="btn white trans table-button"><span class="icon-16 blue n05-eliminar table-button"></span></a>')
	                .on('vclick', function () {
	                	$(this).parent().parent().remove();
	                 });
	                $(data.context.children()[index]).append(deleteButton);
	                
	                var downloadButton = $('<a title="Descargar" class="btn white trans table-button"><span class="icon-16 blue n02-guardar table-button"></span></a>')
	                .attr('target', '_blank').prop('href', file.url)
	                $(data.context.children()[index]).append(downloadButton);
	                
	            } else if (file.error) {
	                var error = $('<span class="text-danger"/>').text(file.error);
	                $(data.context.children()[index])
	                    .append('<br>')
	                    .append(error);
	            }
	        });
	    }).on('fileuploadfail', function (e, data) {
	        $.each(data.files, function (index, file) {
	            var error = $('<span class="text-danger"/>').text('File upload failed.');
	            $(data.context.children()[index])
	                .append('<br>')
	                .append(error);
	        });
	    }).prop('disabled', !$.support.fileInput)
	        .parent().addClass($.support.fileInput ? undefined : 'disabled');
    };
    
    
    $(document).ready(function() {
    	initFileUpload($(this));		
	});
	
	$(document).on('capgemini.ajax.load', function(event, element){
		initFileUpload($(element));
	});	
});
